import { useState, useEffect } from 'react';

const [slides, setSlides] = useState([
  {
    id: 0,
    img: slideImage,
    title: '<span>サンプルテキスト</span><br/><span>サンプル ルテキスト</span><br/><span>サンプルテキスト</span>',
    date: '2019.06.19'
  },
  {
    id: 1,
    img: slideImage,
    title: '<span>サンプルテキスト</span><br/><span>サンプル ルテキスト</span><br/><span>サンプルテキスト</span>',
    date: '2019.07.20'
  },
  {
    id: 2,
    img: slideImage,
    title: '<span>サンプルテキスト</span><br/><span>サンプル ルテキスト</span><br/><span>サンプルテキスト</span>',
    date: '2019.08.21'
  }
]);
const keys = Array.from(Array(slides.length).keys())
slides.push(...slides)

const sleep = (ms = 0) => {
  return new Promise(resolve => setTimeout(resolve, ms))
}

const createItem = (position, idx, activeIdx) => {
  const item = {
      styles: {
        transform: `translateX(${position * slideWidth}vw)`,
        backgroundImage : `url(${position.img})`
      },
      slide: slides[idx]
  }

  switch (position) {
    case length - 1:
    case length + 1:
      item.styles = { ...item.styles }
      break
    case length:
      break
    default:
      item.styles = { ...item.styles, opacity: 0 }
      break
  }

  return item
}

const SlideItem = ({id, title, date, pos}) => {
  const item = createItem(pos, id, current);

  return (
    <li className="slider-item" style={item.styles}>
      <div className="slide-content">
        <h1 className="slide-heading" dangerouslySetInnerHTML={{__html: title}}/>
        <p className="slide-date">{ date }</p>
      </div>
    </li>
  )
}

const Slider = () => {
  const [items, setItems] = useState(keys)
  const [isTicking, setIsTicking] = useState(false)
  const [activeIdx, setActiveIdx] = useState(0)
  const bigLength = items.length

  const slideWidth = 100;
  const slideImage = require('../assets/images/hero.png').default;
  const arrowLeft = require('../assets/images/arrow-left.svg').default;
  const arrowRight = require('../assets/images/arrow-right.svg').default;
  const length = slides.length

  const prevClick = (jump = 1) => {
    if (!isTicking) {
      setIsTicking(true)
      setItems(prev => {
          return prev.map((_, i) => prev[(i + jump) % bigLength])
      })
    }
  }

  const nextClick = (jump = 1) => {
    if (!isTicking) {
        setIsTicking(true)
        setItems(prev => {
          return prev.map(
              (_, i) => prev[(i - jump + bigLength) % bigLength]
          )
        })
    }
  }

  const handleDotClick = idx => {
    if (idx < activeIdx) prevClick(activeIdx - idx)
    if (idx > activeIdx) nextClick(idx - activeIdx)
  }

  // function handleJump(index) {
  //   current < index
  //     ? setTransitionName('slide-next-enter')
  //     : setTransitionName('slide-prev-enter');
  //   setCurrent(index);
  // }

  // function handleSlide(dir) {
  //   setDirection(dir);

  //   dir === 1
  //     ? setTransitionName('slide-next-enter')
  //     : setTransitionName('slide-prev-enter');
  //   const len = slides.length;
  //   setCurrent((current + (dir % len) + len) % len);
  // }

  // useEffect(() => {
  //   setShow(true);
  // });

  return (
    <section className="slider">
      {
        show === true &&
        <>
          <ul className="slider-container">
            {
              slides.map(item => (
                <SlideItem
                  key={item.id}
                  id={item.id}
                  title={item.title}
                  date={item.date}
                  pos={item}
                  activeIdx={activeIdx}
                />
              ))
            }
          </ul>

          <div className="slider-nav">
            <img 
            className="slide-prev"
            src={arrowLeft}
            onClick={() => prevClick()}
            alt="Arrow Left"
            />

            <img 
            className="slide-next"
            src={arrowRight}
            onClick={() => nextClick()}
            alt="Arrow Right"
            />
          </div>
          <ul className="slide-indicators">
            {
              slides.map(item => (
                <li
                  key={item.id}
                  className={(item.id === activeIdx) ? 'active' : ''}
                  onClick={() => handleDotClick(item.id)}
                />
              ))
            }
          </ul>
        </>
      }
    </section>
  )
}

export default Slider;
