import { useState, useEffect } from 'react';


const Slider = () => {
  let [show, setShow] = useState(false);
  let [direction, setDirection] = useState(1);
  let [current, setCurrent] = useState(0);
  let [transitionName, setTransitionName] = useState('fade');

  const slideImage = require('../assets/images/hero.png').default;
  const arrowLeft = require('../assets/images/arrow-left.svg').default;
  const arrowRight = require('../assets/images/arrow-right.svg').default;
  const [slides, setSlides] = useState([
    {
      id: 1,
      img: slideImage,
      title: '<span>サンプルテキスト</span><br/><span>サンプル ルテキスト</span><br/><span>サンプルテキスト</span>',
      date: '2019.06.19'
    },
    {
      id: 2,
      img: slideImage,
      title: '<span>サンプルテキスト</span><br/><span>サンプル ルテキスト</span><br/><span>サンプルテキスト</span>',
      date: '2019.06.19'
    },
    {
      id: 3,
      img: slideImage,
      title: '<span>サンプルテキスト</span><br/><span>サンプル ルテキスト</span><br/><span>サンプルテキスト</span>',
      date: '2019.06.19'
    }
  ]);

  function jump(index) {
    current < index
      ? setTransitionName('slide-next')
      : setTransitionName('slide-prev');
    setCurrent(index);
  }

  function slide(dir) {
    setDirection(dir);

    dir === 1
      ? setTransitionName('slide-next')
      : setTransitionName('slide-prev');
    const len = slides.length;
    setCurrent((current + (dir % len) + len) % len);
  }

  useEffect(() => {
    setShow(true);
  }, []);

  return (
    <div className="slider">
      {
        show === true &&
        <div className="slider-item" key={current} style={{backgroundImage: `url(${slides[current].img})`}}>
          <div className="slide-content">
            <h1 className="slide-title" dangerouslySetInnerHTML={{__html: slides[current].title}}/>
            <p className="slide-date">{ slides[current].date}</p>
          </div>
        </div>
      }
      {/* <div className="slide-nav">
        <img
          className="slide-prev"
          onClick={slide(-1)}
          src={arrowLeft}
          alt="Left Arrow"
        />
        <img
          className="slide-next"
          onClick={slide(1)}
          src={arrowRight}
          alt="Right Arrow"
        />
      </div> */}
      <ul class="slide-dots">
        {
          slides.map(item => (
            <li
            key={item}
            className={ item.id === current || 'active' }
            onClick={jump(item.id)}
            ></li>
          ))
        }
      </ul>
    </div>
  )
}

export default Slider;
