import { useState } from 'react';
import { Link } from 'react-router-dom';
import Button from './Button';

const Register = () => {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirm_password, setConfirmPassword] = useState('');

  function handleEmail(e) {
    setEmail(e.target.value)
  }

  function handlePassword(e) {
    setPassword(e.target.value)
  }

  function handleConfirmPassword(e) {
    setConfirmPassword(e.target.value)
  }

  function handleSubmit(e) {
    if(email && password && confirm_password) {
      console.log('All fields are inputted');
    } else {
      console.log('All fields are required');
    }
  }


  return (
    <section className="l-form login">
      <h1 className="form-title">REGISTER</h1>
      <form className="form">
        <div className="form-group">
          <p className="form-label">Email</p>
          <input className="form-field" value={email} onChange={(e) => (handleEmail(e))} type="email" name="email"/>
        </div>
        <div className="form-group">
          <p className="form-label">Password</p>
          <input className="form-field" value={password} onChange={(e) => (handlePassword(e))} type="password" name="password"/>
        </div>
        <div className="form-group">
          <p className="form-label">Confirm Password</p>
          <input className="form-field" value={confirm_password} onChange={(e) => (handleConfirmPassword(e))} type="password" name="confirm_password"/>
        </div>
        <Button title="REGISTER" onClick={(e) => (handleSubmit(e))} />
        <p className="account">Already have an account?<Link className="account-link" to="/login"> LOGIN HERE</Link></p>
      </form>
    </section>
  ) 
}

export default Register;
