import { Link } from 'react-router-dom';
import { useState } from 'react';

const CreatePost = () => {

  const [title, setTitle] = useState('');
  const [imageUpload, setImageUpload] = useState('');
  const [content, setContent] = useState('');

  function handleOnChangeTitle(e) {
    setTitle(e.target.value)
  }

  function handleOnChangeImage(e) {
    setImageUpload(e.target.value)
  }

  function handleOnChangeTitle(e) {
    setContent(e.target.value)
  }

  return (
    <div className="create-post">
      <div className="create-post-buttons">
        <a href="#" className="create-post-save">Save Post</a>
        <Link className="create-post-cancel" to="/">Cancel</Link>
      </div>
      <div className="l-container">
        <form className="form-post">
          <input type="text" value={Date.now('YYYY.mm.dd')} className="form-post-date" disabled/>
          <input type="text" placeholder="Title" value={title} className="form-post-field-text" required/>
          <div className="form-post-group">
            <img className="form-post-image" src={imageUpload}/>
            <a href="#" className="form-post-upload">UPLOAD IMAGE</a>
          </div>
          <textarea  placeholder="Content" value={content} rows="5" className="form-post-field-textarea" required/>

        </form>
      </div>

    </div>
  )
}

export default CreatePost;
