import { useState } from 'react';

const News = () => {
  const slideImage = require('../assets/images/hero.png').default;
  const [newsList, setNewsList] = useState([
    {
      id: 0,
      img: slideImage,
      title: 'サンプルテキストサンプル ルテキストサンプルテキスト',
      date: '2019.06.19'
    },
    {
      id: 1,
      img: slideImage,
      title: 'サンプルテキストサンプル ルテキストサンプルテキスト',
      date: '2019.06.19'
    },
    {
      id: 2,
      img: slideImage,
      title: 'サンプルテキストサンプル ルテキストサンプルテキスト',
      date: '2019.06.19'
    },
    {
      id: 3,
      img: slideImage,
      title: 'サンプルテキストサンプル ルテキストサンプルテキスト',
      date: '2019.06.19'
    },
    {
      id: 4,
      img: slideImage,
      title: 'サンプルテキストサンプル ルテキストサンプルテキスト',
      date: '2019.06.19'
    },
    {
      id: 5,
      img: slideImage,
      title: 'サンプルテキストサンプル ルテキストサンプルテキスト',
      date: '2019.06.19'
    }

  ]);

  return (
    <section className="news">
      <h2 className="news-heading">News</h2>
      <ul className="news-list">
        {
          newsList.map(item => (
            <li className="news-list-item">
              <div className="news-list-image" style={{backgroundImage: `url(${item.img})`}}></div>
              <p className="news-list-date">{ item.date }</p>
              <h3 className="news-list-title">{ item.title }</h3>
            </li>
          ))
        }
      </ul>
    </section>
  )
}

export default News;
