const authData = {
  isLogged: false,
  role: 'user'
}
const authReducer = (state = authData, action) => {
  switch(action.type) {
    case 'SIGN_IN': 
      return Object.assign({}, state, {
        isLogged: true,
        role: action.payload
      });
    default: 
      return state;
  }
};

export default authReducer;
