import { Link } from 'react-router-dom';
import { useState } from 'react';

const CreatePost = () => {

  const [date, setDate] = useState(handleGetDate());
  const [title, setTitle] = useState('');
  const [imageUpload, setImageUpload] = useState('');
  const [content, setContent] = useState('');

  function handleOnChangeTitle(e) {
    setTitle(e.target.value)
  }

  function handleOnChangeImage(e) {
    setImageUpload(e.target.value)
  }

  function handleOnChangeContent(e) {
    setContent(e.target.value)
  }

  function handleGetDate() {
    let today = new Date();
    let dd = today.getDate();

    let mm = today.getMonth()+1; 
    let yyyy = today.getFullYear();
    if(dd<10) 
    {
        dd='0'+dd;
    } 

    if(mm<10) 
    {
        mm='0'+mm;
    } 

    return today = yyyy + '.' + mm + '.' + dd;
  }

  return (
    <div className="create-post">
      <div className="create-post-button-wrapper">
        <a href="#" className="create-post-button">Save Post</a>
        <Link className="create-post-button" to="/">Cancel</Link>
      </div>
      <div className="l-container">
        <form className="form-post">
          <input type="text" value={date} className="form-post-date" disabled/>
          <input type="text" placeholder="Title" value={title} onChange={(e) => handleOnChangeTitle()}e className="form-post-field-text" required/>
          <div className="form-post-group">
            <img className="form-post-image" src={imageUpload}/>
            <a href="#" className="form-post-upload">UPLOAD IMAGE</a>
          </div>
          <textarea  placeholder="Content" value={content} rows="5" onChange={(e) => handleOnChangeContent(e)} className="form-post-field-textarea" required/>

        </form>
      </div>

    </div>
  )
}

export default CreatePost;
