import { useState } from 'react';
import { Link } from 'react-router-dom';
import Button from './Button';

const Register = () => {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirm_password, setConfirmPassword] = useState('');

  function handleSetField(e) {
    setEmail(e.target.value)
    console.log(email)
  }



  return (
    <section className="l-form login">
      <h1 className="form-title">REGISTER</h1>
      <form className="form">
        <div className="form-group">
          <p className="form-label">Email</p>
          <input className="form-field" value={email} onChange={(e) => (handleSetField(e))} type="email" name="email"/>
        </div>
        <div className="form-group">
          <p className="form-label">Password</p>
          <input className="form-field" value={password} onChange={(e) => (handleSetField(e))} type="password" name="password"/>
        </div>
        <div className="form-group">
          <p className="form-label">Confirm Password</p>
          <input className="form-field" value={confirm_password} onChange={(e) => (handleSetField(e))} type="password" name="confirm_password"/>
        </div>
        <Button title="REGISTER" />
        <p className="account">Already have an account?<Link className="account-link" to="/login"> LOGIN HERE</Link></p>
      </form>
    </section>
  ) 
}

export default Register;
